angular.module("apis-application.filters").filter("stripSlashes", function() {
    return function(input) {
        return input.replace(/\//g, "").replace(/\{/g, "-").replace(/\}/g, "-");
    };
}).filter("nonEnvelopeModels", function() {
    return function(models) {
        return _.filter(models, function(model) {
            return !Swagger.modelIsEnvelope(model.name);
        });
    };
}).filter("noVoid", function() {
    return function(types) {
        return _.filter(types, function(type) {
            return "void" !== type;
        });
    };
}).filter("keys", function() {
    return function(object) {
        return _.keys(object);
    };
}).filter("renameEnvelopes", function() {
    return function(string) {
        return Swagger.modelIsEnvelope(string) ? "envelope" : string;
    };
}).filter("capitalise", function() {
    return function(input) {
        return input.charAt(0).toUpperCase() + input.slice(1);
    };
});